document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("loginId"),t=document.getElementById("password"),s=document.getElementById("questions"),a=document.getElementById("saveBtn");chrome.storage.local.get("easyerp_credentials",a=>{a.easyerp_credentials&&(e.value=a.easyerp_credentials.loginId||"",t.value=a.easyerp_credentials.password||"",s.value=JSON.stringify(a.easyerp_credentials.questions,null,2))}),a.onclick=()=>{try{const a={loginId:e.value,password:t.value,questions:JSON.parse(s.value)};chrome.storage.local.set({easyerp_credentials:a},()=>{alert("Credentials saved!")})}catch(e){alert("Invalid JSON for security questions.")}}});